import React, {Component}from "react";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng
} from "react-places-autocomplete";
import './App.css';
import TextField from '@material-ui/core/TextField';
import { withStyles } from '@material-ui/core/styles';

const style = (theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
});

export class NameSearch extends Component {
    constructor(props) {
        super(props);    
        this.state = {
            address:"",
            coordinates:{
                lat: null,
                lng: null
            }
        };
    }

   handleSelect = async (value) => {
    const results = await geocodeByAddress(value);
    const latLng = await getLatLng(results[0]);
    
    this.setState({
        address: value,
        coordinates: latLng
    }, ()=>{
        this.props.setCoordinates(this.state.coordinates);
    })

  };

  render(){
      let address=this.state.address;
      let type= this.props.type;
      let {classes} = this.props
  return (
    <div>
      <PlacesAutocomplete
        value={address}
        onChange={e=>this.setState({address:e})}
        onSelect={this.handleSelect}
      >
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div>
            <TextField {...getInputProps({ placeholder: "Type address" })} label={type}  variant="outlined" className={classes.formControl} />

            <div>
              {loading ? <div>...loading</div> : null}

              {suggestions.map(suggestion => {
                const style = {
                  backgroundColor: suggestion.active ? "#41b6e6" : "#fff"
                };

                return (
                  <div {...getSuggestionItemProps(suggestion, { style })}>
                    {suggestion.description}
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </PlacesAutocomplete>
    </div>
  );

  }
}

export default withStyles(style)(NameSearch);
