import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, Polyline } from 'google-maps-react';
import taxi from './taxi.png';

const mapStyles = {
  map: {
    position: 'absolute',
    width: '75%',
    height: '100%'
  }
};

export class MapContainer extends Component {
  constructor(props) {
    super(props);
  };
  showMarkers = () => {
    let route = this.props.route;
    console.log('route:', route);
    if (route.length !== 0) {
      return route.map(mrk => (
        <Polyline
          path={route}
          geodesic={true}
          options={{
            strokeColor: '#40E0D0',
            strokeOpacity: 1,
            strokeWeight: 6,
            icons: [{
                offset: '0',
                repeat: '10px'
            }],
          }}
        />
      ))
    }
  }
  render() {
    const style = Object.assign({}, mapStyles.map);
    const fromCoordinates = this.props.fromCoordinates;

    return (
      <div style={style}>
        <Map
          google={this.props.google}
          zoom={12}
          key='map'
          initialCenter={
            {
              lat: 59.33,
              lng: 18.056
            }
          }
        ><Marker
            lat={fromCoordinates.lat}
            lng={fromCoordinates.lng}
            icon={{
              url: taxi,
              scaledSize: new this.props.google.maps.Size(60, 50)
            }}

          />{this.showMarkers()}
        </Map>
      </div>
    );
  }
}
export default GoogleApiWrapper(
  (props) => ({
    apiKey: props.apiKey
  }
  ))(MapContainer)
