import React, { Component } from 'react';
import './App.css';
import MapContainer from './NewMap';
import NameSearch from './NameSearch';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import BzztLogo from './BzztLogo.jpg';


export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toCoordinates: {},
      fromCoordinates: {}
    };
  }
  state = {
    toCoordinates: {},
    fromCoordinates: {}
  };

  setCoordinates = (coordinates, type) => {
    this.setState({
      [type]: coordinates
    })
  }

  handleSubmit= () => {
    console.log(this.state.toCoordinates, this.state.fromCoordinates)

    let rideBody={

      "type": "delivery",
      "from": {
        "latitude": 59.339898,
        "longitude": 18.03958
      },
      "to": {
        "latitude": 59.343933,
        "longitude": 18.055505
      },
      "metadata": {
        "BillingReference": "reference-123"
      }

  }
    fetch('/book_ride', {
      method: 'POST',
      body: rideBody,
      headers: new Headers({
        "Authorization": 'Bearer' + "0E919FC4E1A356007719337034B72C2750FFB388FFF8DF5C1C68536D0A8B8BBB",
        'Content-Type': 'application/json'
      })
    }
    ).then(response => response.json());
  }

  render() {
    return (
      <div>
        <Grid container
          direction="row"
          justify="flex-start"
          alignItems="center">
          <Grid item xs={6} sm={3}>
          <form className="form-wrapper">
            <img src={BzztLogo} alt={'BzztLogo'} />
            <br/><br/>
            <NameSearch setCoordinates={(e) => this.setCoordinates(e, 'fromCoordinates')} type={'From'} />
            <br/><br/>
            <NameSearch setCoordinates={(e) => this.setCoordinates(e, 'toCoordinates')} type={'To'} />
            <br/><br/>
        <Button onClick={()=>this.handleSubmit()} variant="outlined" color="primary">Book Ride</Button>
      </form>
          </Grid>
          <Grid item xs>
            <MapContainer />
          </Grid>
        </Grid>


      </div>
    );

  }
}

export default App;
