import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, Polyline } from 'google-maps-react';
import taxi from './taxi.png';
import stop from './stop.png';

const mapStyles = {
    map: {
      position: 'absolute',
      width: '75%',
      height: '100%'
    }
  };

  showMarkers = () => {
    let route= this.props.route;
    if(route.length !== 0){
      return route.map(mrk => (
        <Polyline
          path={mrk}
          geodesic={true}
          options={{
            strokeColor: 'red',
            strokeOpacity: 1,
            strokeWeight: 4
          }}
        />
      ))
    }
  }

export class NewMap extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {}
    };
    
  render() {
    const style = Object.assign({}, mapStyles.map);
    const fromCoordinates= this.props.fromCoordinates;
  
    return (
        <div style={style}>
      <Map
        google={this.props.google}
        zoom={12}
        
        initialCenter={
          {
            lat: 59.33,
            lng: 18.056
          }
        }
      ><Marker 
          lat={ fromCoordinates.lat}
          lng={fromCoordinates.lng}
          icon={{
            url: taxi,
            scaledSize:  new this.props.google.maps.Size(60,50)
            }}
  
        /> <div>{this.showMarkers()}</div>
      </Map>
      </div>
    );
  }
}
export default GoogleApiWrapper(
  (props) => ({
    apiKey: props.apiKey
  }
))(NewMap)
