import React ,{Component}from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

export class RideStatus extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      rideStatus:{}
    };
  }
  componentDidUpdate(prevProps) {
    if (prevProps.BookRes !== this.props.BookResS) {
      console.log('cardloaded', this.props.BookRes)
      let rideStatusBody = {
        "reference": this.props.BookRes.reference
      };
  
      fetch('https://test.bzzt.se/api/external/ride_status', {
        method: 'POST',
        headers: {
          'Authorization': 'Bearer 0E919FC4E1A356007719337034B72C2750FFB388FFF8DF5C1C68536D0A8B8BBB',
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(rideStatusBody),
      })
        .then(response => {
          if (response.ok) {
            return response.json()
          } else {
            response.text().then(text => {
  
              const error = JSON.parse(text);
              throw alert(error.message)
            })
          }
        })
        .then(data => {
          if (data !== undefined) {
            this.setState({ rideStatus: data})
            console.log('date is:', data)
          }
        })
    }
    else{
      
      console.log('cardloaded not ', this.props.BookRes)
    }
  }
  render(){
    let BookRes=this.props.BookRes;
  return (
    <Card>
      <CardContent>
        <Typography variant="h5" component="h2">
          Refrence: {BookRes !== null ?  BookRes.reference : 'Not booked'}
        </Typography>
        <Typography color="textSecondary">
          ETA: 
          <br/>
          Ride Status: 
        </Typography>
        <Typography variant="body2" component="p">
          Ride State:
          <br />
          Estimated Fare:
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Cancel Ride</Button>
      </CardActions>
    </Card>
  );

  }
}

export default RideStatus;
