import React, { Component } from 'react';
import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';

const mapStyles = {
    map: {
      position: 'absolute',
      width: '75%',
      height: '100%'
    }
  };
  const MyCustomMarker = () => <h1 class="material-icons">place</h1>;
export class NewMap extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {}
    };

    onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null,
        coordinates:[
            {
                "lat": 59.339898,
                "lng": 18.03958
              },
              {
                "lat": 59.343933,
                "lng": 18.055505
              },
        ]
      });
    }
  };
    
  render() {
    const style = Object.assign({}, mapStyles.map);
    const coordinates= this.state.coordinates;
    return (
        <div style={style}>
      <Map
        google={this.props.google}
        zoom={14}
        
        initialCenter={
          {
            lat: 59.33,
            lng: 18.056
          }
        }
      ><MyCustomMarker
  />
      </Map>
      </div>
    );
  }
}
export default GoogleApiWrapper(
    (props) => ({
      apiKey: props.apiKey
    }
  ))(NewMap)
