import React, { Component } from 'react';
import './App.css';
import MapContainer from './NewMap';
import NameSearch from './NameSearch';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import BzztLogo from './BzztLogo.jpg';
import ConformationDialog from './ConformationDialog';


export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toCoordinates: {},
      fromCoordinates: {},
      timeestimate:{},
      openConfDialog: false
    };
  }
  state = {
    toCoordinates: {},
    fromCoordinates: {}
  };

  setCoordinates = (coordinates, type) => {
    this.setState({
      [type]: coordinates
    })
  }

  handleClose= ()=>{
    this.setState({
      openConfDialog: false
    })
  }

  handleSubmit = () => {
    console.log(this.state.toCoordinates, this.state.fromCoordinates)
    let toCoordinates = this.state.toCoordinates;
    let fromCoordinates = this.state.fromCoordinates;
    this.setState({
      openConfDialog: true
    })
    let rideBody = {
      "from": {
        "latitude": toCoordinates.lat,
        "longitude": toCoordinates.lng
      },
      "to": {
        "latitude": fromCoordinates.lat,
        "longitude": fromCoordinates.lng
      }
    }
    fetch('/time_estimate', {
      method: 'POST',
      body: JSON.stringify(rideBody),
      headers: new Headers({
        "Authorization": 'Bearer' + "0E919FC4E1A356007719337034B72C2750FFB388FFF8DF5C1C68536D0A8B8BBB",
        'Content-Type': 'application/json'
      })
    }).then(response => response.json())
    .then(find =>{
      if(find.length !== 0){
        this.setState({timeestimate:find})
      }
    })
    .catch(error => console.log(error));
  }

  render() {
    let openConfDialog = this.state.openConfDialog;

    return (
      <div>
        <Grid container
          direction="row"
          justify="flex-start"
          alignItems="center">
          <Grid item xs={6} sm={3}>
            <form className="form-wrapper">
              <img src={BzztLogo} alt={'BzztLogo'} />
              <br /><br />
              <NameSearch setCoordinates={(e) => this.setCoordinates(e, 'fromCoordinates')} type={'From'} />
              <br /><br />
              <NameSearch setCoordinates={(e) => this.setCoordinates(e, 'toCoordinates')} type={'To'} />
              <br /><br />
              <Button onClick={() => this.handleSubmit()} variant="outlined" color="primary">Book Ride</Button>
              <ConformationDialog  openConfDialog= {openConfDialog} handleClose= {()=>this.handleClose()}/>
            </form>
          </Grid>
          <Grid item xs>
            <MapContainer/>
          </Grid>
        </Grid>


      </div>
    );

  }
}

export default App;
